mapper.update:
update inquery_board
set    inq_readcount=inq_readcount+1
where  inq_num=#{number}

mapper.select:
select * from inquery_board
where    inq_num = #{num}
and      inq_pass = #{pass}

mapper.update:
 update inquery_board
 set  	inq_subject=#{inq_subject},
		inq_content=#{inq_content},
		inq_file=#{inq_file},
		inq_original=#{inq_original}
 where  inq_num=#{inq_num}

mapper.delete:

delete from inquery_board
		where inq_re_ref =  #{inq_re_ref}
		and   inq_re_lev >= #{inq_re_lev}
		and   inq_re_seq >= #{inq_re_seq}
		and   inq_re_seq <= (
								nvl((SELECT min(inq_re_seq)-1
							         FROM   inquery_board
							         WHERE  inq_re_ref = #{inq_re_ref}
							         AND    inq_re_lev = #{inq_re_lev}
							         AND    inq_re_seq > #{inq_re_seq}),
							        (SELECT max(inq_re_seq)
							         FROM   inquery_board
							         WHERE  inq_re_ref = #{inq_re_ref})
								))
