mapper.select:
select count(*)
from board_comment
where board_num = #{board_num}

SELECT
    subquery.*,
    u.user_department,
    u.user_profile_picture,
    u.user_name
FROM (
         SELECT *
         FROM board_comment
         WHERE board_num = #{board_num}
         ORDER BY comm_regdate DESC
             LIMIT #{start}, #{end}
     ) AS subquery
         JOIN users u ON subquery.user_num = u.user_num;
