mapper.update:
UPDATE view_check
SET checked = 1
WHERE announce_board_num = #{annboardNum}
AND user_num = #{loginNum}

UPDATE view_check
SET checked = 0
WHERE announce_board_num = #{annboardNum}
  AND user_num = #{loginNum}

mapper.select:
SELECT checked
FROM view_check
WHERE announce_board_num = #{annboardNum}
AND user_num = #{loginNum}
