mapper.select:
SELECT * FROM ( SELECT ROW_NUMBER() OVER (
ORDER BY trip_date ASC ORDER BY trip_date DESC ORDER BY trip_price ASC ORDER BY trip_price DESC ORDER BY expire_date ASC
) AS rnum, trip_name, trip_no, trip_price, trip_stock, trip_max_stock, reg_date, expire_date, trip_date, file_id, trip_main_img, trip_category FROM trip WHERE status = 'APPROVED' ) AS tripAlias WHERE rnum BETWEEN #{startRow} AND #{endRow}

SELECT * FROM ( SELECT ROW_NUMBER() OVER (
ORDER BY STR_TO_DATE(trip_date, '%Y-%m-%d') ASC ORDER BY STR_TO_DATE(trip_date, '%Y-%m-%d') DESC ORDER BY trip_price ASC ORDER BY trip_price DESC ORDER BY trip_no ASC
) AS rnum, trip_name, trip_no, trip_price, trip_stock, trip_max_stock, reg_date, expire_date, trip_date, file_id, trip_main_img, trip_category FROM trip WHERE trip_category = #{category} AND status = 'APPROVED' ) AS derived_table_alias where rnum BETWEEN #{startRow} AND #{endRow}

SELECT * FROM ( SELECT ROW_NUMBER() OVER (
ORDER BY trip_date ASC ORDER BY trip_date DESC ORDER BY trip_price ASC ORDER BY trip_price DESC ORDER BY expire_date ASC
) AS rnum, trip_name, trip_no, trip_price, trip_stock, trip_max_stock, reg_date, expire_date, trip_date, file_id, trip_main_img, trip_category FROM trip WHERE trip_name LIKE CONCAT('%', #{keyword}, '%') AND status = 'APPROVED' ) AS tripAlias WHERE rnum BETWEEN #{startRow} AND #{endRow}

mapper.update:
UPDATE trip
SET status = #{status}
WHERE trip_no = #{tripNo}
