mapper.select:
SELECT u.user_num,
u.user_name,
u.user_created_at,
u.user_is_approved,
u.user_phone_number,
u.user_email,
u.user_gender,
u.user_auth,
u.user_status,
d.department_name,
p.position_name
FROM user u
JOIN user_departments d ON u.department_id = d.department_id
JOIN user_positions p ON u.position_id = p.position_id


        u.user_is_approved = #{params.user_is_approved}


        AND MATCH(u.user_name) AGAINST(#{params.user_name} IN NATURAL LANGUAGE MODE)


        AND u.department_id = #{params.department_id}


        AND u.position_id = #{params.position_id}


        AND u.user_status = #{params.user_status}


        AND u.user_num IN (
        SELECT ua.user_num
        FROM user_attendance ua
        WHERE ua.check_in_time >= #{params.startDate}
        )



    AND u.user_num IN (
        SELECT ua.user_num
        FROM user_attendance ua
        WHERE ua.check_out_time <= #{params.endDate}
    )



ORDER BY u.user_created_at DESC, u.user_num;
