mapper.select:
SELECT u.user_num, u.user_name, u.user_created_at, u.user_is_approved, u.user_phone_number, u.user_email, u.user_gender, u.user_auth, d.department_name, p.position_name FROM user u JOIN user_departments d ON u.department_id = d.department_id JOIN user_positions p ON u.position_id = p.position_id
d.department_id =#{department_id}
ORDER BY u.user_created_at DESC, u.user_num
