mapper.update:
UPDATE user_attendance
SET check_out_time= #{dateTime}
WHERE user_num = #{userNum}

mapper.select:
select *
from user_attendance
where user_num = #{userNum}
  AND check_in_time BETWEEN #{startDate} AND #{endDate}

SELECT u.user_num, u.user_name, d.department_name, p.position_name, ua.check_in_time, ua.check_out_time, ua.work_time FROM user_attendance ua JOIN user u ON ua.user_num = u.user_num JOIN user_departments d ON u.department_id = d.department_id JOIN user_positions p ON u.position_id = p.position_id
ua.check_in_time BETWEEN #{params.startDate} AND #{params.endDate} AND d.department_id = #{params.department_id}
ORDER BY u.user_num
